package InterviewFinalPreparation;

public class SumOfArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {10,12,23,45};
		int size=a.length;
		int sum=0;
		for(int i=0;i<size;i++)
		{
			sum+=a[i];
		}
		System.out.println(sum);
		

	}

}
