package InterviewFinalPreparation;

public class Unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 7;

		int y = 6*x++;

		System.out.println (" y= " + y);

		int a = 7;

		int b = 6*++a;

		System.out.println (" b= " + b);

		
	}

}
